import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Booking.css';

const BookingPage = () => {
    const { eventId } = useParams();
    const [event, setEvent] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:5000/api/events/${eventId}`)
            .then(response => {
                setEvent(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the event details!', error);
            });
    }, [eventId]);

    if (!event) {
        return <div>Loading...</div>;
    }

    return (
        <div className="booking-container">
            <div className="booking-card">
                <img src={event.eventImage} alt={event.eventTitle} className="booking-img" />
                <div className="booking-details">
                    <h2 className="booking-title">{event.eventTitle}</h2>
                    <p className="booking-date">Date: {new Date(event.eventDate).toLocaleDateString()}</p>
                    <p className="booking-time">Time: {event.eventTime}</p>
                    <p className="booking-location">Location: {event.eventLocation}</p>
                    <p className="booking-price">Ticket Price: ${event.ticketPrice}</p>
                    <button className="booking-btn">Proceed to Payment</button>
                </div>
            </div>
        </div>
    );
};

export default BookingPage;
