import React, { useState } from 'react';
import axios from 'axios'; // Add axios for making API requests

function Order() {
  const [name, setName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [productName, setProductName] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [address, setAddress] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Make an API request to the server
    axios.post('http://localhost:5000/api/bookings/placeOrder', {
      name,
      mobileNumber,
      productName,
      quantity,
      address,
    })
      .then((response) => {
        console.log(response.data);
        // Handle success response
      })
      .catch((error) => {
        console.error(error);
        // Handle error response
      });
  };

  return (
    <div className="booking">
      <center>
        <form className="b1" onSubmit={handleSubmit}>
          <h2>ORDER HERE</h2>
          <label> NAME:</label><br></br>
          <input value={name} onChange={(e) => setName(e.target.value)}></input><br></br>
          <label>MOBILE NUMBER :</label><br></br>
          <input value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)}></input><br></br>
          <label>PRODUCT NAME :</label><br></br>
          <input value={productName} onChange={(e) => setProductName(e.target.value)}></input><br></br>
          <label>QUANTITY :</label><br></br>
          <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)}></input><br></br>
          <label>ADDRESS:</label><br></br>
          <input value={address} onChange={(e) => setAddress(e.target.value)}></input><br></br><br></br>
          <button>PLACE ORDER</button>
        </form>
      </center>
    </div>
  );
}

export default Order;
