import './Login.css';
import { useForm } from "react-hook-form";
import { useNavigate } from 'react-router-dom';


function App() {
  const { register, handleSubmit, formState: { errors }, reset, watch } = useForm();
  const password = watch('password');
  const navigate = useNavigate();
// const onSubmit = async (data) => {
//   try {
//     const response = await fetch('http://localhost:3001/login', {
//       method: 'POST',
//       headers: {
//         'Content-Type': 'application/json', 
//         },
//         body: JSON.stringify(data),
//         }); 

  // Updated onSubmit function to handle login
  const handleClick = () => {
    navigate('/signup'); 
  }



  return (
    <div className='loginmain'>
      <div className="loginform">
        <h2 className='title'>REGISTER FORM</h2>
        <div className="form">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label className='name'>Username :</label><br />
            <input
              className='iname'
              placeholder='NAME'
              {...register("username", {
                required: 'Username is required',
                minLength: { value: 5, message: 'At least 5 characters are required' },
                pattern: { value: /^[a-zA-Z ]+$/, message: 'Only alphabets are allowed' },
              })}
            />
            {errors.username && <div>{errors.username.message}</div>}
            
            <label className='email'>Email :</label><br />
            <input
              className='ieml'
              placeholder='EMAIL'
              {...register("email", {
                required: 'Email is required',
                pattern: { 
                  value: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/,
                  message: 'Invalid email format',
                },
              })}
            />
            {errors.email && <div>{errors.email.message}</div>}
            
            <label className='pass'>Password :</label><br />
            <input
              className='ipass'
              placeholder='PASSWORD'
              type='password'
              {...register("password", {
                required: 'Password is required',
                minLength: { value: 8, message: 'Password must be at least 8 characters long' },
                pattern: { 
                  value: /^(?=.*[!@#$%^&*])/,
                  message: 'Password must contain at least one special character',
                },
              })}
            />
            {errors.password && <div>{errors.password.message}</div>}<br />
            
            <label className='conpass'>Confirm Password :</label><br />
            <input
              className='iconpass'
              placeholder='CONFIRM PASSWORD'
              type='password'
              {...register("confirmPassword", {
                required: 'Confirm Password is required',
                validate: value => value === password || 'Passwords do not match',
              })}
            />
            {errors.confirmPassword && <div>{errors.confirmPassword.message}</div>}<br />
            
            <center><button className='subbt' type="submit">Register</button></center><br />
          </form>
          <center><p>Or</p></center>
          <center><button className='signup' onClick={handleClick}>Login</button></center>
        </div>
      </div>
    </div>
  );
}

export default App;
