import React, { useState } from "react";
import axios from 'axios';
import "./Order.css"

function Order(){
    const [name, setName] = useState('');
    const [number, setNumber] = useState('');
    const [product, setProduct] = useState('');
    const [quantity, setQuantity] = useState('');
    const [address, setAddress] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();
        if (name && number && product && quantity && address) {
            try {
                const response = await axios.post('http://localhost:5000/api/order', {
                    name,
                    number,
                    product,
                    quantity,
                    address
                });
                if (response.status === 201) {
                    setSuccess('Product added successfully');
                    setName('');
                    setNumber('');
                    setProduct('');
                    setQuantity('');
                    setAddress('');
                } else {
                    setError('Failed to add Product');
                }
            } catch (error) {
                setError('Failed to add Product');
                console.error(error);
            }
        } else {
            setError('Please fill in all fields');
        }
    };

    return(
        <div className="booking">
            <div className="b1">
                <h2>ORDER HERE</h2>

                <form onSubmit={handleSubmit}>
                    <label>NAME:</label><br></br>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} required></input><br></br>
                    <label>MOBILE NUMBER :</label><br></br>
                    <input value={number} onChange={(e) => setNumber(e.target.value)} required></input><br></br>
                    <label>PRODUCT NAME :</label><br></br>
                    <input value={product} onChange={(e) => setProduct(e.target.value)} required></input><br></br>
                    <label>QUANTITY :</label><br></br>
                    <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} required></input><br></br>
                    <label>ADDRESS:</label><br></br>
                    <input value={address} onChange={(e) => setAddress(e.target.value)} required></input><br></br><br></br>
                    <button type="submit" onClick={handleSubmit}  >PLACE ORDER</button>
                </form>
            </div>
        </div>
    )
}
export default Order;
