import logo1 from './Business-Event.jpg';
import logo2 from './kpop.jpg';
import logo3 from './food.jpg';
import logo4 from './cricket.jpg';
import React, { useState } from 'react';
import './Slider.css';

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const eventCards = [
    {
      img: logo1,
      title: "Emerging CEO's",
      date: "2022-01-01",
      time: "10:00 AM",
      location: "New York",
    },
    {
      img: logo2,
      title: "BTS-Kpop",
      date: "2022-01-01",
      time: "10:00 AM",
      location: "Korea",
    },
    {
      img: logo3,
      title: "Food festival",
      date: "2022-01-01",
      time: "10:00 AM",
      location: "New Delhi",
    },
    {
      img: logo4,
      title: "India VS England",
      date: "2022-01-01",
      time: "10:00 AM",
      location: "England",
    },
  ];

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % eventCards.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + eventCards.length) % eventCards.length);
  };

  return (
    <div className="slider">
      <button className="prev" onClick={prevSlide}>&#10094;</button>
      <div className="slides">
        {eventCards.map((event, index) => (
          <div
            key={index}
            className={`slide ${index === currentSlide ? 'active' : ''}`}
          >
            <div className="eventcard">
              <img className="eventimg" src={event.img} alt="Event Image" />
              <h2 className="eventtitle">{event.title}</h2>
              <p className="eventdate">Date: {event.date}</p>
              <p className="eventtime">Time: {event.time}</p>
              <p className="eventlocation">Location: {event.location}</p>
              <button className="eventbt">Buy Ticket</button>
            </div>
          </div>
        ))}
      </div>
      <button className="next" onClick={nextSlide}>&#10095;</button>
    </div>
  );
};

export default Slider;
