import React, { useState } from 'react';
import './Booking.css';

const Booking = () => {
  const [selectedEvent, setSelectedEvent] = useState('');
  const [ticketQuantity, setTicketQuantity] = useState(1);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    // Add booking logic here
    alert(`Booking confirmed for ${name} (${email}) - ${ticketQuantity} tickets for ${selectedEvent}`);
  };

  return (
    <div className="booking-container">
      <h1>Book Your Event</h1>
      <form onSubmit={handleBookingSubmit} className="booking-form">
        <div className="form-group">
          <label htmlFor="event">Choose Event:</label>
          <select
            id="event"
            value={selectedEvent}
            onChange={(e) => setSelectedEvent(e.target.value)}
            required
          >
            <option value="">Select an Event</option>
            <option value="Emerging CEO's">Emerging CEO's</option>
            <option value="BTS-Kpop">BTS-Kpop</option>
            <option value="Food festival">Food festival</option>
            <option value="India VS England">India VS England</option>
            <option value="Emerging CEO's">T</option>
            <option value="BTS-Kpop">BTS-Kpop</option>
            <option value="Food festival">Food festival</option>
            <option value="India VS England">India VS England</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="quantity">Number of Tickets:</label>
          <input
            type="number"
            id="quantity"
            value={ticketQuantity}
            onChange={(e) => setTicketQuantity(e.target.value)}
            min="1"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="name">Full Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email Address:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone Number:</label>
          <input
            type="tel"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="booking-btn">Confirm Booking</button>
      </form>
    </div>
  );
};

export default Booking;
