import React, { useState } from 'react';
import './Booking.css';

const BookingPage = ({ events }) => {
    const [selectedEvent, setSelectedEvent] = useState(events[0]?.eventTitle || '');
    const [ticketQuantity, setTicketQuantity] = useState(1);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission logic here (e.g., API call)
        console.log('Booking Details:', {
            eventTitle: selectedEvent,
            eventPrice: events.find(event => event.eventTitle === selectedEvent)?.ticketPrice,
            ticketQuantity,
            name,
            email
        });
    };

    return (
        <div className="booking-container">
            <h2>Book Your Ticket</h2>
            <form onSubmit={handleSubmit} className="booking-form">
                <div className="booking-field">
                    <label>Select Event:</label>
                    <select
                        value={selectedEvent}
                        onChange={(e) => setSelectedEvent(e.target.value)}
                        className="booking-input"
                    >
                        {events.map(event => (
                            <option key={event._id} value={event.eventTitle}>
                                {event.eventTitle}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="booking-field">
                    <label>Number of Tickets:</label>
                    <input
                        type="number"
                        value={ticketQuantity}
                        onChange={(e) => setTicketQuantity(e.target.value)}
                        min="1"
                        required
                        className="booking-input"
                    />
                </div>
                <div className="booking-field">
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        className="booking-input"
                    />
                </div>
                <div className="booking-field">
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        className="booking-input"
                    />
                </div>
                <button type="submit" className="booking-button">Buy Ticket</button>
            </form>
        </div>
    );
};

export default BookingPage;
