import React from "react";
import { useState } from "react";
import axios from 'axios';
import "./Order.css"
function Order(){
    const [name, setname] = useState('');
    const [number, setnumber] = useState('');
    const [product, setproduct] = useState('');
    const [quantity, setquantity] = useState('');
    const [address, setaddress] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if ( name && number && product && quantity && address) {

            alert('Passwords do not match');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/api/events/order', {
                name,
                number,
                product,
                quantity,
                address
            });
            if (response.status === 201) {
                alert('Product addeed successfully');
                setname('');
                setnumber('');
                setproduct('');
                setquantity('');
                setaddress('');

            } else {
                alert('Failed to add Product');
            }
        } catch (error) {
            console.error(error);
        }
    };


 
    return(
        <div className="booking">
            <center>
                <form  className="b1" >
                    <h2>ORDER HERE</h2>
                    <label> NAME:</label><br></br>
                    <input  type="text" value={name} onChange={(e) => setname(e.target.value)} required ></input><br></br>
                    <label>MOBILE NUMBER :</label><br></br>
                    <input value={number} onChange={(e) => setnumber(e.target.value)} required    ></input><br></br>
                    <label>PRODUCT NAME :</label><br></br>
                    <input value={product} onChange={(e) => setproduct(e.target.value)} required></input><br></br>
                    <label>QUANTITY :</label><br></br>
                    <input type="number" value={quantity} onChange={(e) => setquantity(e.target.value)} required></input><br></br>
                    <label>ADDRESS:</label><br></br>
                    <input value={address} onChange={(e) => setaddress(e.target.value)} required></input><br></br><br></br>
                    <button >PLACE ORDER</button>
                </form>
            </center>
        </div>
    )
}
export default Order;
