import React, { useState } from 'react';

const BookingPage = ({ eventTitle, eventPrice }) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission, e.g., send data to backend or store it locally
        console.log('Booking Details:', { eventTitle, eventPrice, name, email });
    };

    return (
        <div style={styles.container}>
            <h2>Book Your Ticket</h2>
            <form onSubmit={handleSubmit} style={styles.form}>
                <div >
                    <label>Event Title:</label>
                    <span>{eventTitle}</span>
                </div>
                <div style={styles.field}>
                    <label>Event Price:</label>
                    <span>{eventPrice}</span>
                </div>
                <div style={styles.field}>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                        style={styles.input}
                    />
                </div>
                <div style={styles.field}>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={styles.input}
                    />
                </div>
                <button type="submit" style={styles.button}>Buy Ticket</button>
            </form>
        </div>
    );
};
export default Booking;
