import React from 'react';
import './Events.css';
import logo1 from './Business-Event.jpg';
import logo2 from './kpop.jpg';
import logo3 from './food.jpg';
import logo4 from './cricket.jpg';

function Event() {
  const events = [
    { img: logo1, title: "Emerging CEO's", date: "2022-01-01", time: "10:00 AM", location: "New York", description: "Join us for an exciting event where emerging CEOs share their insights on leadership, innovation, and the future of business." },
    { img: logo2, title: "BTS-Kpop", date: "2022-01-01", time: "10:00 AM", location: "Korea", description: "Experience the electrifying performance of BTS, one of the most popular K-pop bands in the world." },
    { img: logo3, title: "Food Festival", date: "2022-01-01", time: "10:00 AM", location: "New Delhi", description: "Savor delicious dishes from around the world at our annual food festival." },
    { img: logo4, title: "India VS England", date: "2022-01-01", time: "10:00 AM", location: "England", description: "Watch the thrilling cricket match between India and England." }
  ];

  return (
    <div className='eventmain'>     
      <div className="upcomingevent">
        {events.map((event, index) => (
          <div className="eventcard1" key={index}>
            <img className="eventimg1" src={event.img} alt={`Event Image ${index + 1}`} />
            <h2 className="eventtitle1">{event.title}</h2>
            <p className="eventdate1">Date: {event.date}</p>
            <p className="eventtime1">Time: {event.time}</p>
            <p className="eventlocation1">Location: {event.location}</p>
            <p className='desp1'>{event.description}</p>
            <button className="eventbt1">Buy Ticket</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Event;
