import React, { useState } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import Home from './components/Home.jsx';
import Event from './components/Eventlist.js';
import Book from './components/Booking.jsx';
import Contact from './components/Contact.jsx';
import Log from './components/Login.js';
import AdminAddEvent from './components/AdminAddEvent';

import logo from './components/DK KD Logo.jpg';
import './App.css';

const isAdmin = true;

function App() {
  const [activePage, setActivePage] = useState('home');

  const renderPage = () => {
    switch (activePage) {
      case 'home':
        return <Home />;
      case 'events':
        return <Event />;
      case 'booking':
        return <Book />;
      case 'contact':
        return <Contact />;
      case 'login':
        return <Log />;
      case 'admin':
        return isAdmin ? <AdminAddEvent /> : <Event />;
      default:
        return <Home />;
    }
  };

  return (
    <Router>
      <div>
        <div className="navibar">
          <img className="logo" src={logo} alt="logo" />
          <Link className="home" to="/" onClick={() => setActivePage('home')}>HOME</Link>
          <Link className="event" to="/events" onClick={() => setActivePage('events')}>EVENT LIST</Link>
          <Link className="booking" to="/booking" onClick={() => setActivePage('booking')}>BOOKING</Link>
          <Link className="login" to="/login" onClick={() => setActivePage('login')}>LOGIN</Link>
          <Link className="contact" to="/contact" onClick={() => setActivePage('contact')}>CONTACT</Link>
          {isAdmin && (
            <Link className="admin" to="/admin" onClick={() => setActivePage('admin')}>ADMIN PAGE</Link>
          )}
        </div>

        <div className="content">
          {renderPage()}
        </div>
      </div>
    </Router>
  );
}

export default App;
