import './components/SignUp.css';
import { useForm } from "react-hook-form";

function SignUp() {
  const { register, handleSubmit, formState: { errors }, reset, watch } = useForm();
  const password = watch('password');

  const onSubmit = (data) => {
    console.log(data);
    reset();
  };

  return (
    <div className='signupmain'>
      <div className="signupform">
        <h2 className='title'>SIGN UP FORM</h2>
        <div className="form">
          <form onSubmit={handleSubmit(onSubmit)}>
            <label className='name'>Username :</label><br />
            <input
              className='iname'
              placeholder='NAME'
              {...register("username", {
                required: 'Username is required',
                minLength: { value: 5, message: 'At least 5 characters are required' },
                pattern: { value: /^[a-zA-Z ]+$/, message: 'Only alphabets are allowed' },
              })}
            />
            {errors.username && <div className='error'>{errors.username.message}</div>}
            
            <label className='email'>Email :</label><br />
            <input
              className='ieml'
              placeholder='EMAIL'
              type='email'
              {...register("email", {
                required: 'Email is required',
                pattern: { 
                  value: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/, 
                  message: 'Invalid email format',
                },
              })}
            />
            {errors.email && <div className='error'>{errors.email.message}</div>}
            
            <label className='pass'>Password :</label><br />
            <input
              className='ipass'
              placeholder='PASSWORD'
              type='password'
              {...register("password", {
                required: 'Password is required',
                minLength: { value: 8, message: 'Password must be at least 8 characters long' },
                pattern: { 
                  value: /^(?=.*[!@#$%^&*])/,
                  message: 'Password must contain at least one special character',
                },
              })}
            />
            {errors.password && <div className='error'>{errors.password.message}</div>}<br />
            
            <label className='conpass'>Confirm Password :</label><br />
            <input
              className='iconpass'
              placeholder='CONFIRM PASSWORD'
              type='password'
              {...register("confirmPassword", {
                required: 'Confirm Password is required',
                validate: value => value === password || 'Passwords do not match',
              })}
            />
            {errors.confirmPassword && <div className='error'>{errors.confirmPassword.message}</div>}<br />
            
            <center><button className='subbt' type="submit">Sign Up</button></center><br />
          </form>
          <center><p>Already have an account?</p></center>
          <center><a className='login-link' href='#'>Login</a></center>
        </div>
      </div>
    </div>
  );
}

export default SignUp;
